import React, { useState, useRef } from "react";
import { makeStyles } from '@material-ui/core/styles'
import { Card, Typography } from '@material-ui/core'
import Slider from "react-slick";
import SendIcon from "@material-ui/icons/Send";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import image from '../../../../public/assets/violation.jpg'
import img from '../../../../public/assets/img.jpg'


function Infowindow (props){
    const classes = useStyles()
    const ref = useRef({});
    console.log("ref,ref",ref);

    const [opendialog, setOpendialog] = useState(false);
    const [openImage, setOpenImage] = useState(false);
    const[photoIndex,setPhotoIndex]=useState(0)
    const [opacity, setOpacity] = useState(0);
    const next = () => {
        ref.current.slickPrev();
      };
      const prev = () => {
        ref.current.slickNext();
      };
      const settings = {
        dots: true,
        speed: 500,
        slidesToShow: 1,
      };
    return(

        <div className={classes.Info}>
            <div
              onMouseOver={() => {
                setOpacity(1);
              }}
              onMouseOut={() => {
                setOpacity(0);
              }}
              className={classes.slidercontent}
            >
              <Slider {...settings} className={classes.slider} ref={ref}>
                <div onClick={() => showImage()}>
                  <img className={classes.imgpopup} src={image}></img>
                </div>
                <div>
                  <img className={classes.imgpopup} src={img}></img>
                </div>
                <div>
                  <img className={classes.imgpopup} src={image}></img>
                </div>
              </Slider>
              <div style={{ opacity: opacity }} class={classes.slidebutton}>
                <div className={classes.next} onClick={() => next()}>
                  <NavigateBeforeIcon className={classes.iconbutton} />
                </div>
                <div className={classes.prev} onClick={() => prev()}>
                  <NavigateNextIcon className={classes.iconbutton} />
                </div>
              </div>
            </div>
            <div className={classes.titlepopup}>
              <Typography variant="subtitle1" className={classes.font}>
                <b className={classes.title}>Tuyến đường:</b>
                {/* {props.marker.stress} */}
              </Typography>
              <Typography variant="subtitle1" className={classes.font}>
                <b className={classes.title}>Biển số:</b>
                {/* {props.marker.vehicle} */}
              </Typography>
              <Typography variant="subtitle1" className={classes.font}>
                <b className={classes.title}>Ngày vi phạm:</b>
                {/* {props.marker.date} */}
              </Typography>
              <div
                //   className={classes.infotitlepopup}
                onClick={() => dialogtable()}
              >
                <Button
                  variant="outlined"
                  color="primary"
                  className={classes.infotitlepopup}
                >
                  chi tiết <SendIcon className={classes.icon} />
                </Button>
                {/* <a className={classes.infotitlepopup}>thông tin chi tiết...</a> */}
              </div>
            </div>
          </div>

    )
}
export default Infowindow
const useStyles = makeStyles(() => ({
    slider: {
        width: 450,
      },
      slidercontent: {
        width: "100%",
        position: "relative",
      },
      slidebutton: {
        transition: "0.2s",
        position: "absolute",
        top: "50%",
        width: "100%",
        display: "flex",
        justifyContent: "space-between",
      },
      imgpopup: {
        width: "450px",
      },
      title: {
        fontWeight: 500,
        fontFamily: "Nunito, sans-serif",
      },
      dialog: {
        height: "100%",
        width: "100%",
      },
      imagepopup: {
        width: 350,
      },
      infotitlepopup: {
        cursor: "pointer",
        color: "#3c16ce",
        float: "right",
        marginTop: "-40px",
      },
      icon: {
        fontSize: 14,
        marginLeft: 5,
      },
      titlepopup: {
        padding: 20,
      },
      next: {
        width: 30,
        height: 30,
        borderRadius: "50%",
        transition: "0.5s",
        color: "black",
        cursor: "pointer",
        border: "none",
        backgroundColor: "white",
        "&:hover": {
          color: "black",
          backgroundColor: "white",
        },
      },
      prev: {
        width: 30,
        height: 30,
        borderRadius: "50%",
        transition: "0.5s",
        color: "black",
        border: "none",
        cursor: "pointer",
        backgroundColor: "white",
        "&:hover": {
          color: "black",
          backgroundColor: "white",
        },
      },
      iconbutton: {
        marginTop: 2,
        marginLeft: 2,
      },
      font: {
        fontFamily: "Nunito, sans-serif",
      },
      table: {
        height: "80%",
      },
}))
