import { ViolationTypes } from "../../action_types/index";
import api from './api'
import { combineReducers } from "redux";
const INITIAL_STATE = {
  showInfowindow: {
    id: null,
    info: null,
  },
};
const reducer_search_violation = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ViolationTypes.SHOW_INFOWINDOW_VIOLATION:
      return {
        ...state,
        showInfowindow: {
          id: action.payload.id,
          info: action.payload,
        },
      };
    case ViolationTypes.CLEAR_INFOWINDOW_VIOLATION:
      return {
        ...state,
        showInfowindow: {
          id: null,
        },
      };
    default:
      return state;
  }
};
export default combineReducers({api,reducer_search_violation});
