    //   lat: "16.074250",
    //   lng: "108.223653",
    //   image: "",
    //   vehicle: "43A-11495",
    //   type: "Dừng xe nơi cấm dừng cấm đỗ",
    //   date: "31/10/2020",
    //   stress: "Đường Trần phú,Quận hải châu",
    //   Processingunit: "Sở giao thông vận tải đà nẵng",
    //   typeVehicle: "Ô tô",
    // },
  ];
  const [map, setMap] = useState(null);
  const shapeRef = useRef(null);
  const [infoWindowShape, setInfoWindowShape] = useState(null);
  const [openImage, setOpenImage] = useState(false);
  const [opendialog, setOpendialog] = useState(false);
  const[photoIndex,setPhotoIndex]=useState(0)
  const images = [
    {image:image},
    {image:img},
  ];

  // console.log(props.Infowindow);

  useEffect(() => {
    let value = vehicle.filter((data) => data.id === props.Infowindow.id);
    // console.log("value",value);

    if (value.length) {
      let e = { ...value[0] };
      if (e.path.length % 2 !== 0) {
        let index = Math.ceil(e.path.length / 2);
        e.latLng = e.path[index];
      } else {
        let pointFirst;
        let pointLast;
        if (e.path.length < 3) {
          pointFirst = e.path[0];
          pointLast = e.path[1];
        } else {
          let i = e.path.length / 2;
          pointFirst = e.path[i];
          pointLast = e.path[i + 1];
        }
        let lat = (pointFirst.lat + pointLast.lat) / 2;
        let lng = (pointFirst.lng + pointLast.lng) / 2;

        e.latLng = { lat, lng };
      }
      showInfoWindowShape(e, e);
    }
  }, [props.Infowindow]);

  const clearInfoWindow = () => {
    if (infoWindowShape) {
      infoWindowShape.open(null);
    }
  };
  const handleClose = () => {
    setOpendialog(false);

  };
  const next = () => {
    ref.current.slickPrev();
    // console.log("ref,ref", ref);
  };
  const prev = () => {
    ref.current.slickNext();
  };
  const showImage = () => {
    setOpenImage(true);
  };
  const [opacity, setOpacity] = useState(0);
  // console.log(opacity);

  const showInfoWindowShape = (e, value) => {
    clearInfoWindow();
    const settings = {
      dots: true,
      speed: 500,
      slidesToShow: 1,
    };
    const slide = (
      <React.Fragment>
        <div
          onMouseOver={() => {
            // console.log("vaoffafaf");
            setOpacity(1);
          }}
          onMouseOut={() => {
            setOpacity(0);
          }}
          className={classes.slidercontent}
          id="slider"
        >
          <Slider {...settings} className={classes.slider} ref={ref}  onClick={() => showImage()}>
            <div>
              <img className={classes.imgpopup} src={image}></img>
            </div>
            <div>
              <img className={classes.imgpopup} src={img}></img>
            </div>
            <div>
              <img className={classes.imgpopup} src={image}></img>
            </div>
          </Slider>
          <div className={classes.slidebutton}>
            <div className={classes.next} onClick={() => next()}>
              <NavigateBeforeIcon className={classes.iconbutton} />
            </div>
            <div className={classes.prev} onClick={() => prev()}>
              <NavigateNextIcon className={classes.iconbutton} />
            </div>
          </div>
        </div>
      </React.Fragment>
    );
    const button = (
      <React.Fragment>
        <div
          //   className={classes.infotitlepopup}
          onClick={() => dialogtable()}
          id="button"
        >
          <Button
            variant="outlined"
            color="primary"
            className={classes.infotitlepopup}
          >
            chi tiết <SendIcon className={classes.icon} />
          </Button>
        </div>
      </React.Fragment>
    );
    const Popup = <CustomInfoWindow opacity={opacity} />;
    let infoWindow = new window.google.maps.InfoWindow({
      content: ``,
    });

    if (map) {
      let content = ReactDOMServer.renderToString(Popup);

      infoWindow.setPosition(e.latLng);
      infoWindow.setContent(content);
      infoWindow.open(map);

      window.google.maps.event.addListener(infoWindow, "domready", function () {
        ReactDOM.render(
          React.Children.only(slide),
          document.getElementById("slide")
          // document.getElementById("button")
        );
      });
      window.google.maps.event.addListener(infoWindow, "domready", function () {
        ReactDOM.render(
          React.Children.only(button),
          document.getElementById("button")
        );
      });
      setInfoWindowShape(infoWindow);
    }
  };
  const dialogtable = () => {
    setOpendialog(true);
  };
  const mapEvent = (ref) => {
    if (ref) {
      setMap(ref.context.__SECRET_MAP_DO_NOT_USE_OR_YOU_WILL_BE_FIRED);
    }
  };

  return (
    <MapOptions>
      <GoogleMap
        zoom={13}
        defaultCenter={{ lat: 16.04319399907483, lng: 108.21515312158203 }}
        ref={mapEvent}
        // onClick={(e) => handleMap(e)}
      >
        {vehicle.length
          ? vehicle.map((marker, index) => {
              return (
                <ShapeNoparking
                  marker={marker}
                  key={index}
                  id={marker.id}
                  // ref={ref}
                  handleClose={()=>handleClose()}
                  opendialog={opendialog}
                  shapeRef={shapeRef}
                />
                // <MarkerComponent
                //   marker={marker}
                //   key={index}
                //   // id={marker.id}
                //   lat={marker.lat}
                //   lng={marker.lng}
                // />
              );
            })
          : null}
          {
          openImage?(
            <Lightbox
            mainSrc={images[photoIndex].image}
            nextSrc={images[(photoIndex + 1) % images.length]}
            prevSrc={images[(photoIndex + images.length - 1) % images.length]}
            onCloseRequest={() => (setOpenImage(false))}
            onMovePrevRequest={() =>

                setPhotoIndex((photoIndex + images.length - 1) % images.length)
              }
            onMoveNextRequest={() =>

                setPhotoIndex((photoIndex + 1) % images.length)
              }
          />
          ):null
      }
      </GoogleMap>

    </MapOptions>
  );
}
