import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { TableHead, TableRow, TableCell, Checkbox } from "@material-ui/core";
import TableContainer from "@material-ui/core/TableContainer";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import { DataGrid } from "@material-ui/data-grid";
import { useDemoData } from "@material-ui/x-grid-data-generator";
import Pagination from "@material-ui/lab/Pagination";
import images from "../../../public/assets/images.gif";
import Typography from "@material-ui/core/Typography";
import img2 from "../../../public/assets/img.jpg";
import LogoData from "../../../public/assets/edit.gif";
// import { Scrollbars } from 'react-custom-scrollbars';
import { RequestStatus } from "../../../utils/constants";
function HeaderTable(props) {
  const classes = useStyles();
  const { listViolation, updateSelectViolation, violationSelected } = props;
  const { data } = useDemoData({
    dataSet: "Commodity",
    rowLength: 100,
    maxColumns: 6,
  });
  const itemActive = (e, historyRow) => {
    props.showInfowindow(historyRow);
  };
  const onChangePagination = (e, page) => {
    const { plate } = props;
    props.getSearchViolation({ plate, page });
  };
  return (
    <>
      <div
        className={classes.tableWraper}
        style={{
          flex: 1,
          overflowY: "scroll",
        }}
      >
        <Table stickyHeader className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align="center">
                <Typography className={classes.tabletitle}> Ảnh </Typography>
              </TableCell>
              <TableCell align="center">
                {" "}
                <Typography className={classes.tabletitle}>
                  {" "}
                  Biển số{" "}
                </Typography>
              </TableCell>
              <TableCell align="center">
                <Typography className={classes.tabletitle}>
                  Lỗi vi phạm{" "}
                </Typography>
              </TableCell>
              <TableCell align="center">
                {" "}
                <Typography className={classes.tabletitle}>
                  Tuyến đường vi phạm{" "}
                </Typography>
              </TableCell>
            </TableRow>
          </TableHead>
          {/* {props.status === RequestStatus.STARTED ? ( */}
            <div className={classes.loadingrequest}>
              <img src={images} className={classes.loadingimg} />
            </div>
          {/* ) : null} */}
          {props.vehicle.data?.length ? (
            props.vehicle.data.map((historyRow) => (
              <TableBody>
                <TableRow
                  onClick={(e) => itemActive(e, historyRow)}
                  className={
                    props.Infowindow.id === historyRow.id
                      ? classes.tablerow
                      : classes.untablerow
                  }
                >
                  <TableCell align="center" className={classes.tablecell}>
                    <img
                      src={historyRow.thumnail}
                      className={classes.imagepopup}
                    ></img>
                  </TableCell>
                  <TableCell align="left">
                    <b className={classes.font}>{historyRow.numberPlate}</b>

                    <Typography
                      variant="body1"
                      color="textSecondary"
                      className={classes.colorfont}
                    >
                      {" "}
                      {historyRow.vehicleType}
                    </Typography>
                  </TableCell>
                  <TableCell align="left">
                    <Typography className={classes.font}>
                      {historyRow.type}
                    </Typography>
                    <Typography
                      variant="body1"
                      color="textSecondary"
                      className={classes.colorfont}
                    >
                      {historyRow.date}
                    </Typography>
                  </TableCell>
                  <TableCell align="center">
                    <Typography className={classes.font}>
                      {historyRow.address}
                    </Typography>
                  </TableCell>
                </TableRow>
              </TableBody>
            ))
          ) : (
            <div className={classes.icongif}>
              {" "}
              <img src={LogoData} className={classes.imggif}></img>
            </div>
          )}
        </Table>
      </div>
      {props.vehicle.data?.length ? (
        <div className={classes.pagination}>
          <Pagination
            count={props.vehicle.total / props.vehicle.perPage}
            showFirstButton
            showLastButton
            onChange={(e, page) => onChangePagination(e, page)}
            siblingCount={1}
          />
        </div>
      ) : null}
    </>
  );
}

export default HeaderTable;

const useStyles = makeStyles((theme) => ({
  table: {
    width: "100%",
    // [theme.breakpoints.down('sm')]: {
    //   // backgroundColor: 'red',
    //   width:'80%'
    // },
  },
  tableWraper: {
    overflowX: "auto",
  },
  pagination: {
    width: "100%",
    display: "flex",
    padding: "10px 0",
    justifyContent: "center",
    boxShadow: "5px 2px 6px 3px #bbbbbb78",
  },
  tablerow: {
    background: "#00000014",
    // display: 'flex',
    cursor: "pointer",
    // marginBottom: 5,
  },
  tabletitle: {
    fontFamily: "Nunito, sans-serif",
    fontSize: 16,
  },
  untablerow: {
    cursor: "pointer",
  },
  imagepopup: {
    width: 130,
    height: 100,
  },
  font: {
    fontFamily: "Nunito, sans-serif",
    // fontSize: 16,
  },
  colorfont: {
    fontSize: 14,
    // color
  },
  tablecell: {
    padding: 0,
  },
  icongif: {
    width: 100,
  },
  imggif: {
    width: 500,
    marginTop: 100,
    marginLeft: 100,
  },
}));
