import React from "react";
import { makeStyles, Paper, Typography, Button } from "@material-ui/core";
import img from "../../public/assets/logo.png";
import Link from "next/link";
import {useRouter} from 'next/router';
let backgroundActive = "linear-gradient(45deg, #E91E63, #202e7d)";
const ManageNavbar = () => {
  //   const classes = makeStyles();
  const classes = useStyles();

  //   const match = useRouteMatch();
  //   const history = useHistory();
    const rounter = useRouter();
    const className = classes.link|| ''

    if(rounter.pathname === href) {
        className = `${className} selected`
    }
  return (
    <Paper square className={classes.header}>
      <img src={img}></img>
      {/* <Typography component="h2" className={classes.title}>
        Sở Giao Thông Đà Nẵng
      </Typography> */}
      <div className={classes.menu}>
        <div className={classes.button}>
          <Link href='/index' className={classes.link}>Trang Chủ</Link>
        </div>
        <div className={classes.button}>
          <Link  href='/violation' className={classes.link}>Vi Phạm</Link>
        </div>
        <div className={classes.button}><Link href='/violation' className={classes.link}>Tin Tức</Link></div>
      </div>
    </Paper>
  );
};

export default ManageNavbar;
const useStyles = makeStyles((theme) => ({
  header: {
    height: 70,
    zIndex: 1,
    // background: '#0a0944',
    background: "#FFFFFF",
    color: "white",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0 50px",
  },
  link:{
      color:'black'
  },
  button: {
    fontSize: 18,
    position: "relative",
    color: "black",
    textTransform: "capitalize",
    marginRight: 22,
    transition: ".2s ease-in-out",
    fontFamily: "Nunito, sans-serif",
    "&.active": {
      background: 'white',
      color: "black",
      "&::after": {
        opacity: "1",
        visibility: "visible",
        width: "100%",
      },
    },

    "&::after": {
      content: '""',
      position: "absolute",
      width: 0,
      height: 3,
      background: "#1b5bd4",
      bottom: 0,
      left: 0,
      opacity: 0,
      transition: ".2s ease-in-out",
      visibility: "hidden",
    },
    "&::before": {
      content: '""',
      position: "absolute",
      width: 0,
      height: "100%",
      background: "#1b5bd4s",
      bottom: 0,
      left: 0,
      opacity: 0,
      transition: ".2s ease-in-out",
      visibility: "hidden",
      zIndex: -1,
      borderRadius: 4,
    },
    "&:hover:after": {
      opacity: 1,
      visibility: "visible",
      width: "100%",
    },
    "&:hover": {
      color: "#1b5bd4",
    },
    "&:hover:before": {
      opacity: 1,
      visibility: "visible",
      width: "100%",
    },
  },

  title: {
    fontWeight: 500,
    textTransform: "uppercase",
    fontSize: 18,
  },
}));
