import React,{useState} from "react";
import { makeStyles, Paper, Typography, Button } from "@material-ui/core";
import img from "../../public/assets/logo.png";
import Link from "next/link";
import {useRouter} from 'next/router';

let backgroundActive = "linear-gradient(45deg, #E91E63, #202e7d)";

const NavLink = (props ) => {
//   const classes = useStyles();
const [className , setClassName] = useState(props.className);
    const rounter = useRouter();

    // console.log(props.href);
    console.log(rounter.pathname);
    if(rounter.pathname === props.href) {
        setClassName(`${className} active`);
    }
  return (
  <Link className={className} href={props.href}>{props.children}</Link>
  );
};

export default NavLink;
