      path: [
            { lat: 16.066145689229703, lng: 108.18494029608158 },
            { lat: 16.0621866873228, lng: 108.17953296270755 },
          ],
      path: [
        { lat: 16.081238661120793, lng: 108.16631503668244 },
        { lat: 16.073486130125332, lng: 108.18519778814701 },
      ],
    },
    // {
