  if (infoWindowShape) {
      infoWindowShape.open(null)
  }
}
const [opacity, setOpacity] = useState(0);
const showInfoWindowShape = (e, value) => {
  clearInfoWindow()
  const slide =(
    <React.Fragment>
      <div
              onMouseOver={() => {
                setOpacity(1);
              }}
              onMouseOut={() => {
                setOpacity(0);
              }}
              className={classes.slidercontent}
            >
              <Slider {...settings} className={classes.slider} ref={ref}>
                <div onClick={() => showImage()}>
                  <img className={classes.imgpopup} src={image}></img>
                </div>
                <div>
                  <img className={classes.imgpopup} src={img}></img>
                </div>
                <div>
                  <img className={classes.imgpopup} src={image}></img>
                </div>
              </Slider>
              <div style={{ opacity: opacity }} class={classes.slidebutton}>
                <div className={classes.next} onClick={() => next()}>
                  <NavigateBeforeIcon className={classes.iconbutton} />
                </div>
                <div className={classes.prev} onClick={() => prev()}>
                  <NavigateNextIcon className={classes.iconbutton} />
                </div>
              </div>
            </div>
    </React.Fragment>
  )
  const Popup = <CustomInfoWindow/>
  let infoWindow = new window.google.maps.InfoWindow({
      content: ``,
  })

  if (map) {
      let content = ReactDOMServer.renderToString(Popup)

      infoWindow.setPosition(e.latLng)
      infoWindow.setContent(content)
      infoWindow.open(map)

      window.google.maps.event.addListener(infoWindow, 'domready', function () {
          // ReactDOM.render(React.Children.only(button), document.getElementById('buttonAction'))
      })

      setInfoWindowShape(infoWindow)
  }
}
const mapEvent = (ref) => {

  if (ref) {
      setMap(ref.context.__SECRET_MAP_DO_NOT_USE_OR_YOU_WILL_BE_FIRED)
  }
}


  return (
    <MapOptions>
      <GoogleMap
        zoom={13}
        defaultCenter={{ lat: 16.04319399907483, lng: 108.21515312158203 }}
        ref={mapEvent}
        // onClick={(e) => handleMap(e)}
      >
        {vehicle.length
          ? vehicle.map((marker, index) => {
              return (
                <ShapeNoparking
                  marker={marker}
                  key={index}
                  id={marker.id}
                  // ref={ref}
                  shapeRef={shapeRef}
                />
                // <MarkerComponent
                //   marker={marker}
                //   key={index}
                //   // id={marker.id}
                //   lat={marker.lat}
                //   lng={marker.lng}
                // />
              );
            })
          : null}

      </GoogleMap>
    </MapOptions>
  );
}
export default MapViolation;
