        fontWeight: 500,
        fontFamily: "Nunito, sans-serif",
      },
      dialog: {
        height: "100%",
        width: "100%",
      },
      imagepopup: {
        width: 350,
      },
      infotitlepopup: {
        cursor: "pointer",
        color: "#3c16ce",
        float: "right",
        marginTop: "-40px",
      },
      icon: {
        fontSize: 14,
        marginLeft: 5,
      },
      titlepopup: {
        padding: 20,
      },
      next: {
        width: 30,
        height: 30,
        borderRadius: "50%",
        transition: "0.5s",
        color: "black",
        cursor: "pointer",
        border: "none",
        backgroundColor: "white",
        "&:hover": {
          color: "black",
          backgroundColor: "white",
        },
      },
      prev: {
        width: 30,
        height: 30,
        borderRadius: "50%",
        transition: "0.5s",
        color: "black",
        border: "none",
        cursor: "pointer",
        backgroundColor: "white",
        "&:hover": {
          color: "black",
          backgroundColor: "white",
        },
      },
      iconbutton: {
