        className = `${className} selected`
    }
  return (
    <Paper square className={classes.header}>
      <img src={img}></img>
      {/* <Typography component="h2" className={classes.title}>
        Sở Giao Thông Đà Nẵng
      </Typography> */}
      <div className={classes.menu}>
        <div className={classes.button}>
            <NavLink href ='/index'> 
            Trang Chủ
            </NavLink>
          {/* <Link href='/index' className={className}>Trang Chủ</Link> */}
        </div>
        <div className={classes.button}>
          <Link  href='/violation' className={className}>Vi Phạm</Link>
        </div>
        <div className={classes.button}><Link href='/violation' className={className}>Tin Tức</Link></div>
      </div>
    </Paper>
  );
};

export default ManageNavbar;
const useStyles = makeStyles((theme) => ({
  header: {
    height: 70,
    zIndex: 1,
    // background: '#0a0944',
    background: "#FFFFFF",
    color: "white",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0 50px",
  },
  link:{
      color:'black'
  },
  button: {
    fontSize: 18,
    position: "relative",
    color: "black",
    textTransform: "capitalize",
    marginRight: 22,
    transition: ".2s ease-in-out",
    fontFamily: "Nunito, sans-serif",
    "&.active": {
      background: 'white',
      color: "black",
      "&::after": {
        opacity: "1",
        visibility: "visible",
        width: "100%",
      },
    },

    "&::after": {
      content: '""',
      position: "absolute",
      width: 0,
      height: 3,
      background: "#1b5bd4",
      bottom: 0,
      left: 0,
      opacity: 0,
      transition: ".2s ease-in-out",
      visibility: "hidden",
    },
    "&::before": {
      content: '""',
      position: "absolute",
      width: 0,
      height: "100%",
      background: "#1b5bd4s",
      bottom: 0,
      left: 0,
      opacity: 0,
      transition: ".2s ease-in-out",
      visibility: "hidden",
      zIndex: -1,
      borderRadius: 4,
    },
    "&:hover:after": {
      opacity: 1,
      visibility: "visible",
      width: "100%",
    },
    "&:hover": {
      color: "#1b5bd4",
    },
    "&:hover:before": {
      opacity: 1,
      visibility: "visible",
      width: "100%",
    },
  },

  title: {
    fontWeight: 500,
    textTransform: "uppercase",
    fontSize: 18,
  },
}));
