import { combineReducers } from "redux";
import { initialRequestState, handleRequest } from "../helpers";
import { SEARCH_VIOLATION } from "../../action_types";

function getSearchViolation(state = initialRequestState(), action) {
  return handleRequest(
    SEARCH_VIOLATION.GET_SEARCH_VIOLATION_REQUEST,
    SEARCH_VIOLATION.GET_SEARCH_VIOLATION_SUCCESS,
    SEARCH_VIOLATION.GET_SEARCH_VIOLATION_FAILURE,
    state,
    action
  );
}
export default combineReducers({
  getSearchViolation,
});
