import React,{useState} from "react";
import { Marker, InfoWindow } from "react-google-maps";
import iconmk from "../../../../public/assets/ic_parking.png";
import { makeStyles } from "@material-ui/core/styles";
import image from "../../../../public/assets/violation.jpg";
import Typography from "@material-ui/core/Typography";
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogActions from '@material-ui/core/DialogActions';
function MarkerComponent(props) {
  var iconmarker = {
    url: iconmk,
    scaledSize: new window.google.maps.Size(40, 40),
  };
  const classes = useStyles();
  const [opendialog,setOpendialog]=useState(false)
  const dialogtable =()=>{
    setOpendialog(true)
  }
  return (
    <Marker
      position={{
        lat: parseFloat(props.marker.lat),
        lng: parseFloat(props.marker.lng),
      }}
      icon={iconmarker}
    >
      {props.Infowindow.lat === props.lat &&
      props.Infowindow.lng === props.lng ? (
        <InfoWindow>
          <div className={classes.Info}>
            <Typography variant="subtitle1">
              <b className={classes.title}>Tuyến đường:</b>{props.marker.stress}
            </Typography>
            <Typography variant="subtitle1">
              <b className={classes.title}>Biển số:</b>{props.marker.vehicle}
            </Typography>
            <Typography variant="subtitle1">
              <b className={classes.title}>Ngày vi phạm:</b>{props.marker.date}
            </Typography>
            <div className={classes.infotitlepopup} onClick={()=>dialogtable()}>
                <a href="#">thông tin chi tiết...</a>
            </div>
            <img className={classes.imgpopup} src={image}></img>
          </div>
        </InfoWindow>
      ) : null}
      {
          opendialog?(
            <Dialog open={opendialog}  aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
            {/* <DialogActions>
              <Button onClick={handleClose} color="primary">
                Cancel
              </Button>
              <Button onClick={handleClose} color="primary">
                Subscribe
              </Button>
            </DialogActions> */}
          </Dialog>
          ):null
      }
    </Marker>
  );
}
export default MarkerComponent;
const useStyles = makeStyles((theme) => ({
  Info: {},
  imgpopup: {
    width: "100%",
  },
  title:{
      fontWeight:500,
  }
}));
